//	mods.filter: (struct)
//
// |:filter|.
//
//	mods.filter.pattern: (string)
//
// Filter pattern. Empty string if there is no filter.
//
//	mods.filter.force: (bool)
//
// Whether filter is inverted or not.
//
//	mods.silent: (bool)
//
// silent
//
//	mods.emsg_silent: (bool)
//
// emsg_silent
//
//	mods.unsilent: (bool)
//
// unsilent
//
//	mods.sandbox: (bool)
//
// sandbox
