//	mods.noautocmd: (bool)
//
// noautocmd
//
//	mods.browse: (bool)
//
// browse
//
//	mods.confirm: (bool)
//
// confirm
//
//	mods.hide: (bool)
//
// hide
//
//	mods.horizontal: (bool)
//
// horizontal
//
//	mods.keepalt: (bool)
//
// keepalt
//
//	mods.keepjumps: (bool)
//
// keepjumps
//
//	mods.keepmarks: (bool)
//
// keepmarks
//
//	mods.keeppatterns: (bool)
//
// keeppatterns
//
//	mods.lockmarks: (bool)
//
// lockmarks
//
//	mods.noswapfile: (bool)
//
// noswapfile
//
//	mods.tab: (int)
//
// -1 when omitted.
//
//	verbose: (int)
//
// -1 when omitted.
//
//	vertical: (bool)
//
// vertical
//
//	split: (string)
//
// Split modifier string, is an empty string when there's no split modifier. If there is a split modifier it can be one of:
